$(window).on('scroll', function () {
    var wSize = $(window).width();
    if (wSize > 1 && $(this).scrollTop() > 1) {
        $('#sticky-header').addClass('sticky');
    }
    else {
        $('#sticky-header').removeClass('sticky');
    }
});




